var apiKey = 'MDI4Njg2NTg3MDE0ODAwNjMxMDZjMDEyNA000';

var programs = null;

var channels = [
    { title: 'Animals/动物', id: 1132 },
    { title: 'Architecture/建筑', id: 1142 },
    { title: 'Business/商业', id: 1006 },
    { title: 'Education/教育类', id: 1013 }
];
var app = angular.module('myApp', ['ngAnimate']);
app.filter('convertS', function() { //可以注入依赖
    return function(s) {
        if(!s){
            return '00:00';
        }
        seconds = parseInt(s % 60);
        minutes = parseInt((s / 60) % 60);
        hours = parseInt(((s / 60) / 60) % 24);

        var min, hrs;
        if(seconds<10)  sec="0"+seconds;
        else            sec= ""+seconds;
        if(minutes<10)  min="0"+minutes;
        else            min= ""+minutes;
        if(hours<10)    hrs="0"+hours;
        else            hrs= ""+hours;

        if(hours == 0)  return min+":"+sec;
        else    return hrs+":"+min+":"+sec;
    }
});
app.directive('leftNav', function() {
    return {
        restrict: 'A',
        scope: false,
        link: function($scope, element, attrs){
            var el = document.getElementsByTagName('body')[0];
            var hammertime = new Hammer(el);
            
            hammertime.on('swipeleft', function(ev) {
                $scope.$apply(function () {
                    $scope.nav.collapse = true;
                });
            });
            hammertime.on('swiperight', function(ev) {
                $scope.$apply(function () {
                    $scope.nav.collapse = false;
                });
            });
        }
    };
});
app.factory('audio', ['$document', function($document){
    var audio = $document[0].createElement('audio');
    return audio;
}]);

app.factory('nprService', ['$rootScope', '$http', function($rootScope, $http){
    var baseUrl = 'http://api.npr.org/query?';
    var fields = 'relatedLink,title,byline,text,audio,image,pullQuote,all';

    var doRequest = function(nprUrl){
        return  $http({
            method: 'JSONP',
            url: nprUrl + '&apiKey=' + apiKey + '&callback=JSON_CALLBACK'
        })
    };

    var loadProgramsByTopicId = function(id){
        var url = baseUrl + 'id='+id+'&fields='+fields+'&sort=dateDesc&output=JSON';

        return new Promise(function(resolve, reject){
                doRequest(url).then(function success(response){
                    //console.log(response);
                    var list = response.data.list.story;
                    var programs = new Array();
                    for(var i in list){
                        if(list[i].audio && list[i].audio[0].format.mp4){
                            programs.push(list[i]);
                        }
                    }
                    resolve(programs);
                }, function error(response){
                    reject(response);
                });
        });
    };

    return {
        loadPrograms:function(id){
            return loadProgramsByTopicId(id);
        }
    };
}]);
app.factory('player', ['audio', '$rootScope', function(audio, $rootScope){
    var player = {
        current: null,
        currentIndex: 0,
        progress: 0,
        progress_percent: 0,
        playing: false,
        ready: false,

        playFirst: function(){
            this.play(programs[this.currentIndex], true);
        },
        next: function(){
            this.currentIndex ++;
            if(this.currentIndex >= programs.length){
                this.currentIndex = 0;
            }
            this.play(programs[this.currentIndex]);
        },
        prev: function(){
            this.currentIndex --;
            console.log(this.currentIndex);
            if(this.currentIndex <= 0 ){
                this.currentIndex = programs.length - 1;
            }
            this.play(programs[this.currentIndex]);
        },
        play: function(program, stop) {
            if (player.playing) 
                player.stop();
            
            if(program){
                var url = program.audio[0].format.mp4.$text;
                player.current = program;
                audio.src = url;
            }else{
                // play current music
            }
            
            if(stop !== true){
                audio.play();
                player.playing = true;
            }
        },
        stop: function(){
            if(player.playing){
                audio.pause(); // stop playback
                player.ready = player.playing = false; // clear the state
                //player.current = null;
            }
        },
        currentTime: function(){
            return audio.currentTime;
        },
        duration: function(){
            return audio.duration;
        }
    };
    audio.addEventListener('canplay', function(e){
        console.log('canplay');
        $rootScope.$apply(function(){
            player.ready = true;
        });
    });
    audio.addEventListener('timeupdate', function(e){
        console.log('timeupdate');
        $rootScope.$apply(function(){
            player.progress = player.currentTime();
            var percent = player.progress / player.duration() * 100;
            player.progress_percent = Math.round(percent*100)/100;
        })
    });
    audio.addEventListener('ended', function(e){
        $rootScope.$apply(player.stop());
    });
    return player;
}]);
